mapper.update:
UPDATE boards
SET step = step + 1
WHERE ref = #{ref}
AND step > #{step};

mapper.select:
SELECT articleno, userid, subject, content, hit, registerTime, ref, step, depth
FROM boards
WHERE type = #{type}
ORDER BY ref DESC, step ASC, depth ASC LIMIT #{itemsPerPage}

mapper.update:
update boards
set hit = hit + 1
where articleno = #{articleNo}

UPDATE boards
SET subject = #{subject},
    content = #{content}
WHERE articleNo = #{articleNo}

mapper.select:
SELECT COUNT(*)
FROM boards
where type = #{type}
