mapper.select:
select count(userId)
from members
where userId = #{userId}

select userName, userId, userPwd, userSalt, emailId, emailDomain, joinDate, token
from members
where userId = #{userId} and userPwd = #{userPwd}

select userSalt
from members
where userId = #{userId}

mapper.update:
update members
set userId = #{memberDto.userId}, userName = #{memberDto.userName},  emailId = #{memberDto.emailId}, emailDomain = #{memberDto.emailDomain}
where userId = #{userId}

mapper.select:
select userName, userId, emailId, emailDomain, joinDate
from members
where userId = #{userId}

mapper.update:
update members
set token = #{token}
where userId = #{userId}

mapper.select:
select token
from members
where userId = #{userId}

mapper.update:
update members
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userId}
