mapper.select:
SELECT articleno, userid, subject, content, hit, registerTime
FROM boards
WHERE type = #{type}
ORDER BY articleno DESC LIMIT #{itemsPerPage}

mapper.update:
update boards
set hit = hit + 1
where articleno = #{articleNo}

UPDATE boards
SET subject = #{subject},
    content = #{content}
WHERE articleNo = #{articleNo}

mapper.select:
SELECT COUNT(*)
FROM boards
where type = #{type}
